import React, { useEffect, useState } from "react";
import styled from "styled-components";
import Header from "../home/Header";
import BoxInfoAccount from "./BoxInfoAccount";
import axios from "axios";
import imguser from "../../img/icons/user.png"
import imgpointer from "../../img/icons/pointer.png"

export default function Account() {

  const [users, setUser] = useState([])

  async function getUser() {
    try {
      const resp = await axios.get("http://localhost:4000/user", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });

      setUser(resp.data);
      console.log(resp.data)
    } catch (error) {}
  }

  useEffect(() => {
    getUser()
  }, [])

    return (
      <>
        <Header />
        <StyledAccount>
          <BoxInfoAccount />
          <div className="myaccount">
            <div className="title">
              <img src={imguser} /> <h1>Minha Conta</h1>
            </div>
            <div className="infos">
              <h2>Informações de Acesso</h2>
              {users.length > 0
                ? users.map((user) => (
                    <div className="infouser">
                      <p>
                        <strong>name:</strong> {user.name}
                      </p>
                      <p>
                        <strong>email:</strong> {user.email}
                      </p>
                    </div>
                  ))
                : ""}
            </div>
          </div>
          <div className="enderecos">
            <div className="titleenderecos">
              <img src={imgpointer} />
              <h2>Endereços Cadastrados</h2>
              <button>GERENCIAR ENDEREÇOS</button>
            </div>
            <div className="enderecopadrao">
              <h2>Endereço Padrão</h2>
            </div>
          </div>
        </StyledAccount>
      </>
    );
}

const StyledAccount = styled.div`
  width: 100vw;
  min-height: 100vh;
  background-color: #303030;
  padding-top: 100px;
  padding-left: 25px;
  display: flex;

  .myaccount {
    margin-left: 30px;
    background-color: #515151;
    flex: 1;
    align-self: flex-start;
    border-radius: 5px;
    padding: 10px;
  }

  .myaccount .title {
    display: flex;
    align-items: center;
    width: 100%;
  }

  .myaccount .title img {
    width: 25px;
  }

  .myaccount .title h1 {
    font-size: 22px;
    color: white;
    font-family: "Roboto";
    margin-top: 5px;
    margin-left: 10px;
  }

  .infos h2 {
    font-size: 20px;
    color: white;
    margin-top: 20px;
    font-family: "Roboto";
  }

  .infouser {
    margin-top: 30px;
  }

  .infouser strong {
    font-size: 18px;
    font-weight: 500;
    margin-right: 5px;
  }

  .infouser p {
    color: white;
    font-size: 14px;
  }

  .enderecos {
    background-color: #515151;
    align-self: flex-start;
    flex: 1;
    margin-left: 20px;
    border-radius: 5px;
    padding: 10px;
  }

  .enderecos .titleenderecos {
    display: flex;
  }

  .enderecos .titleenderecos img {
    width: 25px;
    height: 25px;
  }

  .enderecos .titleenderecos h2 {
    font-size: 22px;
    font-family: "Roboto";
    color: white;
  }

  .enderecos .titleenderecos button {

  }
`;
