import styled from "styled-components";
import React from "react";
import axios from "axios";
import { toast } from "react-toastify";
import { useContext, useState } from "react";
import UserContext from "../../context/UserContext";

export default function BoxProduct(params) {
  const [products, setProducts] = useState([]);
  const [total, seTotal] = useState();

  const context = useContext(UserContext);

  async function getCart() {
    try {
      const resp = await axios.get("http://localhost:4000/cart", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });

      setProducts(resp.data);
      context.setContcar(resp.data.length);
    } catch (error) {}
  }

  async function addToCart(img_url, name, price, quantity, stock) {
    try {
      await axios.post(
        "http://localhost:4000/cart",
        {
          img_url,
          name,
          price,
          quantity,
          stock,
        },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );
      toast("🦄 Produto adicionado ao carrinho com sucesso!", {
        position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "light",
      });
      context.setRefresh(true);
    } catch (error) {
      console.log(error);
    }
  }

  async function removeToCart(id) {
    try {
      await axios.delete(`http://localhost:4000/cart?id=${id}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });
      toast("🦄 Produto removido do carrinho com sucesso!", {
        position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "light",
      });
      context.setRefresh(true);
    } catch (error) {
      console.log(error);
    }
  }

  async function calculaPrecos(products) {
    let totalproduct = 0;
    const item = products.map(
      ({ price, quantity }) => (price / 100) * quantity
    );

    for (let i = 0; i <= item.length - 1; i++) {
      const val = item[i];
      totalproduct += val;
    }

    seTotal(totalproduct.toFixed(2));

    context.setTotalProducts(totalproduct.toFixed(2));
  }

  setTimeout(() => {
    calculaPrecos(products);
  }, 5);

  useEffect(() => {
    getCart();
  }, [context.refresh]);

  return (
    <StyledBoxProduct key={params.key}>
      <div
        className="container"
        onClick={() => {
          params.add
            ? addToCart(
                params.product.img_url,
                params.product.name,
                params.product.price,
                1,
                params.product.stock
              )
            : removeToCart(params.product.id);
        }}
      >
        <div className="image">
          <img src={params.product.img_url} alt="imgurl" />
        </div>
        <div className="informations">
          <h1>{params.product.name}</h1>
          <h2>{params.product.description}</h2>
          <h3>
            de <strong>R$ {params.product.price / 100}</strong> por:
          </h3>
          <h4></h4>
        </div>
      </div>
    </StyledBoxProduct>
  );
}

const StyledBoxProduct = styled.div`
  .container {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    margin-bottom: 40px;
    margin-top: 60px;
    background-color: #ffee7c;
    padding: 30px;
    width: 100%;
    max-width: 300px;
    cursor: pointer;
  }

  .image {
    width: 150px;
    height: 150px;
    background-color: white;
    margin-bottom: 10px;
  }

  img {
    padding: 10px;
    width: 150px;
    height: 150px;
  }

  .informations {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
  }

  .informations h1 {
    font-size: 16px;
    text-transform: uppercase;
