import React from "react";
import styled from "styled-components";
import { useEffect, useState, useContext } from "react";
import UserContext from "../context/UserContext";
import axios from "axios";
import Header from "../home/Header";
import { ToastContainer } from "react-toastify";
import InfoProducts from "./BoxInfoProducts";
import clearCart from "../../img/icons/clearcart.png"

export default function Cart() {
  const [products, setProducts] = useState([]);
  const [subtotal, setSubtotal] = useState()
  const [total, seTotal] = useState();
  const [ vazio, setVazio ] = useState(false)

  const context = useContext(UserContext);

  async function getCart() {
    try {
      const resp = await axios.get("http://localhost:4000/cart", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });

      setProducts(resp.data);
      context.setContcar(resp.data.length);
      context.setRefresh(false);
    } catch (error) {}
  }

  async function deleteAllCart() {
    try {
      const resp = await axios.delete("http://localhost:4000/cart/deletecart", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });
      context.setContcar(resp.data.length);
      context.setRefresh(true);
    } catch (error) {}
  }

  async function calculaPrecos(products) {

    let totalproduct = 0
    const item = products.map(({ price, quantity }) => (price / 100) * quantity);

    for(let i = 0; i <= item.length - 1; i++){
      const val = item[i]
      totalproduct += val 
    }

    seTotal(totalproduct.toFixed(2))
    setSubtotal(totalproduct.toFixed(2))
  }

  useEffect(() => {
    context.contcar < 1 ? setVazio(true) : setVazio(false);
  }, [])

  setTimeout(() => {
    calculaPrecos(products);
  }, 5)

  useEffect(() => {
    getCart();

  }, [context.refresh]);

  return (
    <>
      <Header />
      <ToastContainer autoClose={2500} pauseOnHover={false} />
      <StyledCart vazio={vazio}>
        <div className="cart">
          <div className="container-info-products">
            {products.length > 0 ? (
              products.map((product, key) => (
                <InfoProducts setVazio={setVazio} product={product} key={key} />
              ))
            ) : (
              <div className="naoexisteproduto">
                <h2>NÃ£o existe produtos no seu carrinho!</h2>
              </div>
            )}
            <div onClick={() => { deleteAllCart(); setVazio(true) }} className="clearCart">
              <img src={clearCart} />
              <p>LIMPAR CARRINHO</p>
            </div>
          </div>

        </div>
      </StyledCart>
    </>
  );
}

const StyledCart = styled.div`
  width: 100vw;
  min-height: 100vh;
  background-color: #303030;
  padding-top: 130px;

  .cart {
    display: flex;
    justify-content: center;
    padding: 0 0 0 15px;
    width: 100%;
  }

